export default {
  data: () => ({
    dialog: false,
    headers: [
      { text: "Имя файла", value: "calories" },
      { text: "Размер", value: "size" },
      { text: "Дата модификации", value: "modificationDate" }
    ]
  }),

  computed: {
    formTitle() {
      return this.editedIndex === -1 ? "New Item" : "Edit Item";
    }
  },

  watch: {
    dialog(val) {
      val || this.close();
    }
  },

  created() {
    this.initialize();
  },

  methods: {
    uploadFiles() {
      this.$refs.file.click();
    },
    async submitFile() {
      this.file = this.$refs.file.files[0];
      let formData = new FormData();
      formData.append("file", this.file);
      this.progress = true;

      await this.$store.dispatch("UploadSotridnikTemporalyMo", formData);
      if (this.getSotridnikTemporalyMo.Status != 200) {
        this.$store.dispatch(
          "ErrorMessage",
          this.getSotridnikTemporalyMo.Errors
        );
      }
      if (this.getSotridnikTemporalyMo.Status == 200) {
        //Сохранить распарсенный список
        let model = {};
        model.List = this.getSotridnikTemporalyMo.Result;
        model.Emploeyer = this.nameEmployer;
        model.Unit = this.selectedUnit ? this.selectedUnit : null;
        model.IsHome = this.isHome;
        model.DateStart = this.DateStart;
        model.DateEnd = this.DateEnd;
        model.Description = this.Description;
        model.EmployerId = this.employerId;

        await this.$store.dispatch("AddListSotridnikTemporalyMo", model);

        //Обновить список
        await this.refresh();
      }
      this.progress = false;
    },
    initialize() {
      this.desserts = [
        {
          name: "Frozen Yogurt",
          calories: 159,
          fat: 6.0,
          carbs: 24,
          protein: 4.0
        },
        {
          name: "Ice cream sandwich",
          calories: 237,
          fat: 9.0,
          carbs: 37,
          protein: 4.3
        },
        {
          name: "Eclair",
          calories: 262,
          fat: 16.0,
